import React, { Component } from 'react'
import ItemDetail from './ItemDetail'
import Additem from './AddItem'
import { v4 as uuidv4 } from 'uuid'
class ListItems extends Component {
    constructor(props) {
        super (props)
        this.state = {
            isEdit: false,
        }
        this.onSave = this.onSave.bind(this)
    }
    onSave(data){
        var task = {
            id : uuidv4(),
            name : data.name,
            level : data.level,
            status : data.status,
        }
        this.props.onSave(task)
    }
    render() {
        // Get values from taks
        var tasks = this.props.tasks
        // Loop
        var elementsTasks = tasks.map((task, index) => {
            if (task.status === 'open'){
            return <ItemDetail key={task.id} index={index} task={task} onSave={this.onSave}></ItemDetail>
            }
            return null

        }
        )
        return (
            <div className="ListItem">

                <div class="panel panel-info">
                    <div class="panel-heading">
                        <h3 class="panel-title">Panel title</h3>
                    </div>
                    <div class="panel-body">

                        <table class="table table-hover">
                            <thead>
                                <tr>
                                    <th style={{ "width": "5%" }}>#</th>
                                    <th style={{ "width": "61%", 'textAlign': 'center' }}>Name</th>
                                    <th style={{ 'textAlign': 'center' }}>Level</th>
                                    <th style={{ 'textAlign': 'center' }}>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                    {elementsTasks}
                                    <Additem onSave = {this.onSave}></Additem>
                            </tbody>
                        </table>

                    </div>
                </div>

            </div>)
    }
}
export default ListItems
