import React, { Component } from 'react'
import EditItem from './EditItem'
class ItemDetail extends Component {
    constructor(props) {
        super (props)
        this.state = { 
            isEditing : false 
        }
    }
    ShowEdit = () => {
        this.setState(
            { isEditing : true }
        )
    }
    render() {
        var {task, index} = this.props
        return (
            <tr className="ItemDetail">

                <td>{index + 1}</td>
                <td>{task.name}</td>
                <td>
                    <span class={task.level === 'high' ? 'label label-danger' : task.level === 'low' ? 'lalbel label-success' : 'label label-warning'}>{task.level === 'high'? 'High' : task.level === 'low' ? 'Low' : 'Medium'}</span>
                </td>
                <td>
                    <button type="button" class="btn btn-warning" onClick={ this.ShowEdit }>Edit</button>
                    <button type="button" class="btn btn-danger">Delete</button>
                </td>

            </tr >)
    }
}
export default ItemDetail
