import React, { Component } from 'react'

class AddItem extends Component {
    constructor(props) {
        super (props)
        this.state = {
            name : '',
            level : 'high',
            status : 'open',
        }
        this.onSave = this.onSave.bind(this)
    }
    onChange(event){
        var target = event.target
        var name = target.name
        var value = target.value
        this.setState({
            [name] : value
        })
    }
    onSave(event){
        this.props.onSave(this.state)
    }
    render() {
        return (

                <tr className="AddItem">

                    <td></td>
                    <td><input onChange={(e) => this.onChangeInput(e)} type="text" name="name" id="input" class="form-control" onChange={this.onChange}/></td>
                    <td>
                        <select name="level" id="inputSelectType" class="form-control" required="required">
                            <option value="high">High</option>
                            <option value="medium">Medium</option>
                            <option value="low">Low</option>
                        </select>
                    </td>
                    <td>
                        <button type="button" class="btn btn-default">Cancel</button>
                        <button type="button" class="btn btn-success" onClick={this.onSave}>Save</button>
                    </td>
                </tr>
        )
    }
}
export default AddItem
