import React, { Component } from 'react'
import Additem from './AddItem'
class ItemDetail extends Component {
    constructor(props) {
        super (props);
        this.state = {
            'name' : '',
            'level' : 'high',
            'status' : 'open',
            'edit' : true,
        }
        this.Edit = this.Edit.bind(this)
        this.onChange = this.onChange.bind(this)
    }
    Edit() {
        if(this.state.edit){
            this.setState({
                edit : false
            })
        }
        else{
            this.setState({
                edit: true
            })
        }
        console.log(this.state.edit)
    }
    onChange(){

    }
    render() {
        var { task, index } = this.props
        // Show items
        if(this.state.edit){
        return (
            <tr className="ItemDetail">

                <td>{index + 1}</td>
                <td>{task.name}</td>
                <td>
                    <span class={task.level === 'high' ? 'label label-danger' : task.level === 'low' ? 'lalbel label-success' : 'label label-warning'}>{task.level === 'high' ? 'High' : task.level === 'low' ? 'Low' : 'Medium'}</span>
                </td>
                <td>
                    <button type="button" class="btn btn-warning" onClick={this.Edit}>Edit</button>
                    <button type="button" class="btn btn-danger">Delete</button>
                </td>

            </tr >)
        }
        // Show edit items
        return (
            <tr className="EditItem">
                <td></td>
                <td><input type="text" name="TaskName" id="input" class="form-control" value={this.state.name} required="required" onChange={this.onChange} /></td>
                <td>
                    <select name="SelectLevel" id="inputSelectType" class="form-control" required="required" value={this.state.level} onChange={this.onChange}>
                        <option value="">High</option>
                        <option value="">Medium</option>
                        <option value="">Low</option>
                    </select>
                </td>
                <td>
                    <button type="button" class="btn btn-default" onClick={this.Edit}>Cancel</button>
                    <button type="button" class="btn btn-success">Save</button>
                </td>
            </tr>
        )
    }
}
export default ItemDetail
