import React, { Component } from 'react';
import './App.css';
import AddItem from './components/AddItem'
import ListItems from './components/ListItems'
import Search from './components/Search'
import Sort from './components/Sort'
import Title from './components/Title'
import { v4 as uuidv4 } from 'uuid'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [

      ]
    }
  }
  // Get items from Local Storage
  componentWillMount() {
    if(localStorage && localStorage.getItem('tasks')){
      var tasks = JSON.parse(localStorage.getItem('tasks'))
      this.setState(
        { tasks : tasks }
      )
    }
  }
  // Gen data
  GenerateData = () => {
    var tasks = [
      {
        id: uuidv4(),
        name: 'A',
        status: "high",
      },
      {
        id: uuidv4(),
        name: 'B',
        status: 'low'
      },
      {
        id: uuidv4(),
        name: 'C',
        status: 'medium'
      }
    ]
    this.setState({
      tasks: tasks
    })
    localStorage.setItem('tasks', JSON.stringify(tasks))
  }

  render() {
    var tasks = this.state.tasks
    return (
      <div className="App">

        <div class="container">

          <div class="row">
            <div class="col-xs-12 col-sm-12 col-md-12 col-lg-12 title">
              <Title></Title>
            </div>
          </div>

          <div class="row">
            <div class="col-xs-5 col-sm-5 col-md-5 col-lg-5 search">
              <Search></Search>
              <Sort></Sort>

              <button type="button" class="btn btn-primary" onClick={this.GenerateData}>Generate Data</button>

            </div>

            <div class="col-xs-5 col-sm-5 col-md-5 col-lg-5 col-md-offset-2 AddItem">
              <AddItem></AddItem>
            </div>

            <div class="row">
              <div class="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                <ListItems tasks = {tasks}></ListItems>

              </div>
            </div>

          </div>


        </div>


      </div>)
  }
}
export default App
